import {ACTIONS} from '../actions/action'

const countReducers = (state, {type, payload}) => {
    switch (type) {
        case ACTIONS.INCREMENT:
            return state + payload;
        case ACTIONS.DECREMENT:
            return state - payload;
        case ACTIONS.RESET:
            return state = 0; // or the payload


        default:
            return state;
    }
}

export default countReducers
