import {useReducer} from 'react'
import countReducer from '../reducers/countReducer'
import { ACTION } from '../actions/action'

const CounterTwo = () => {
    const [count, countDispatch] = useReducer(countReducer, 0)

    const x = 5;
    const y = 3;


  return (
    <div>
       <button onClick={() => countDispatch({type: ACTION.INCREMENT, payload: x})}>INCREMENT</button>
        <button onClick={() => countDispatch({type: ACTION.DECREMENT, payload: y})}>DECREMENT</button>
        <button onClick={() => countDispatch({type: ACTION.RESET, payload: 0})}>RESET</button>
        <h2>{count}</h2>
    </div>
  )
}

export default CounterTwo
